mapper.update:
UPDATE edu
edu_day = #{eduDay} , edu_name = #{eduName} , edu_price = #{eduPrice} , edu_teacher = #{eduTeacher} , edu_member = #{eduMember} , edu_intro = #{eduIntro} , edu_start_date = #{eduStartDate} , edu_end_date = #{eduEndDate} , edu_start_time = #{eduStartTime} , edu_end_time = #{eduEndTime} , edu_state = #{eduState} , edu_grade = #{eduGrade} , edu_subject = #{eduSubject} , edu_rest = #{eduMember} ,
WHERE edu_no = #{eduNo}

UPDATE edu
edu_rest = #{eduRest} , edu_state = #{eduState} ,
WHERE edu_no = #{eduNo}

mapper.select:
SELECT distinct * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT e.edu_academy_name , e.edu_no, e.edu_name, e.edu_start_date , e.edu_price, e.edu_state, e.edu_rest FROM edu e WHERE e.edu_academy_code = #{searchAcademyCode}
AND edu_state = #{searchEduState} and edu_name LIKE '%${searchKeyword}%'
AND edu_name LIKE '%${searchKeyword}%'
AND edu_state = #{searchEduState} and edu_price LIKE '%${searchKeyword}%'
AND edu_price LIKE '%${searchKeyword}%'
AND edu_state = #{searchEduState}
ORDER BY edu_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM( SELECT edu_name, edu_price 
  								FROM edu
  								WHERE edu_academy_code = #{searchAcademyCode}
  									
											
												AND edu_state = #{searchEduState} and edu_name LIKE '%${searchKeyword}%'
											
											
												AND edu_name LIKE '%${searchKeyword}%'
											
											
								 				AND edu_state = #{searchEduState} and edu_price LIKE '%${searchKeyword}%'
											
											
								 				AND edu_price LIKE '%${searchKeyword}%'
											
											
											
												AND edu_state = #{searchEduState}
											
									
									)
